import  React from'react';
import TextField from "@material-ui/core/TextField";
import jsPDF from 'jspdf';
import ReactDOMServer from 'react-dom/server';



import './App.css';


function App() {


    const calcmultiply = (newValues) => {
    const first = document.querySelector('#CashReceived').value;
    const second = document.querySelector('#Denomination').value;


alert(newValues.target.elements.cashreceived.value);
alert(newValues.target.elements.Denomination.value);

    alert(first);

     // const { first,second} = newValues;
    const newMultiply = parseInt(first)*parseInt(second);
    document.querySelector('.amount').value=parseInt(newMultiply);
    //setMultiply(newMultiply)
    } 

    const styles = {
      fontFamily: "sans-serif",
      textAlign: "center"
    };
    const colstyle = {
      width: "30%"
    };
    const tableStyle = {
      width: "100%"
    };
    const divStyle = {
      lineheight: "10px"
    };

    const generatePDF = (event) => {




     const Prints = () => (


      <div>
        <h3> Cash Details:</h3>

        <div style={divStyle}>Cash Received:{event.target.elements.cashreceived.value}</div>
        <div style={divStyle}>Denomination:{event.target.elements.Denomination.value}</div>
        <div style={divStyle}>Amount:{event.target.elements.amount.value}</div>


        <table id="tab_customers" class="table table-striped" style={tableStyle}>
          <colgroup>
            <col span="1" style={colstyle} />
            <col span="1" style={colstyle} />
          </colgroup>
          <thead>
            <tr class="warning">
              <th>Cash Received</th>
              <th>Denomination</th>

              <th>Amount</th>

            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{event.target.elements.cashreceived.value}</td>
              <td>{event.target.elements.Denomination.value}</td>

              <td>{event.target.elements.total.value}</td>
            </tr>
          </tbody>
        </table>

      </div>
    );


      // const doc = new jsPDF();

      const string = ReactDOMServer.renderToString(<Prints />);
      const pdf = new jsPDF("p", "mm", "a4");

     // renderToString(<Prints />);
     // doc.fromHTML(ReactDOMServer.renderToString(<Prints />));
    //  doc.save("myDocument.pdf");

      pdf.fromHTML(string);
      pdf.save("mydocument.pdf");
    }


    return(

        <div className="form">

          <form onSubmit={generatePDF}>
          <fieldset>
            <h3> Cash Details:</h3>


            <div className="formInputs">
            <TextField id="standard-basic " label="Cash Received" variant="standard"  onChange={(e)=> calcmultiply(e.target.value)} placeholder="Cash received" name="cash received"  className='cash received'/>

           </div>

           <div className="formInputs">
           <TextField id="standard-basic" label="Denomination" variant="standard"  name="denomination"  onChange={(e)=> calcmultiply(e.target.value)} placeholder="denomination" className='Denomination'/>



           </div>

           <div className="formInputs">

           <TextField id="standard-basic" label="Amount" variant="standard" name="total"  className="total"/>

           </div>













           </fieldset>




           <button variant="contained" >Submit</button>


          </form>
        </div>
      )
      }

    export default App;
