import  React from'react';
import TextField from "@material-ui/core/TextField";


import './App.css';


function App() {

  /* const calculations = () => {

    const [values,setValues]=useState({first:"",second:"",multiply:""})
    //const [first,setFirst]=useState('')
    //const [second,setSecond]=useState('')
    //const [multiply,setMultiply]=useState('')

    //setValues(newValues)
    calcmultiply(newValues)
    //calcfirst(newValues)
    //calcSecond(newValues)


    } */
    const calcmultiply = (newValues) => {
    const first = document.querySelector('#ratePerGram').value;
    const second = document.querySelector('#weight').value;

     // const { first,second} = newValues;
    const newMultiply = parseInt(first)*parseInt(second);
    document.querySelector('#total').value=parseInt(newMultiply);
    //setMultiply(newMultiply)
    } 


  return(


    <div className="form">

      <form>
      <fieldset>
        <h3> Billing Details:</h3>
        <div className="formInputs">
           <TextField id="standard-basic" label="Name" variant="standard" />
          </div>
          <div className="formInputs">
          <TextField id="standard-basic" label="Phone No." variant="standard" />
         </div>
         <div className="formInputs">
         <TextField id="standard-basic" label="Name" variant="standard" />
         </div>
         <div className="formInputs">             
      <label>
           Aadhar No.  :
           <input number="number"/>
         </label>
         </div>
         <div className="formInputs">
         <label>
           Address:
           <input state="address"/>
         </label>
         </div>
         <div className="formInputs">
         <label>
           State:
           <input state="state"/>
         </label>
         </div>
         <div className="formInputs">


         <label> Item:
           <input item="item" />
          </label>
          </div>
          <div className="formInputs">


         <label> Date:
           <input type ="date" />
          </label>
          </div>

          <div className="formInputs">


         <label> Days:
           <input days="days" />
          </label>
          </div>

          <div>
          <div className="formInputs">

         <label>
            Rate Per Gram:
           <input type ="number" id="ratePerGram" onChange={(e)=> calcmultiply(e.target.value)} placeholder="Rate per gram"/>
         </label>
         </div>

         <div className="formInputs">

         <label>
           Weight:
           <input type ="number" id="weight" onChange={(e)=> calcmultiply(e.target.value)} placeholder="weight"/>
           {/* <input type ="text" onChange={(e)=> setOperation(e.target.value)} placeholder="operation"/> */}



         </label>
         </div>






         </div>

         <div className="formInputs">
         <label>

           Quantity:
           <input number="number"/>
         </label>
         </div>

         <div className="formInputs">

         <label>
           Amount:
           <input number="number" id="total"/>
         </label>
         </div>


       </fieldset>



       <button variant="contained">Submit</button>



      </form>
    </div>
  )
  }
export default App;
